import { Component, Inject } from '@angular/core';
import {
  MatButtonModule,
  MatFormFieldModule,
  MatInputModule,
  MatRippleModule,
  MatLabel,
  MatHint,
  MatInput,
  MatButton
} from '@angular/material';
import {FormControl, FormGroup} from "@angular/forms";
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs/internal/Observable';
import { BehaviorSubject } from 'rxjs';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  private subject = new BehaviorSubject<Repo[]>([]);

  repos$: Observable<Repo[]> = this.subject.asObservable();

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) {

     /*
    http.get<Repo[]>('https://localhost:7204/api/Repos').subscribe(result => {
      this.repos = result;
    }, error => console.error(error));

     */
  }

  form = new FormGroup({
    searchTerm: new FormControl('')
  })


  onClick() {
    console.log('onClick')
    if (this.form.valid) {
      console.log(this.form.value.searchTerm);
    }

    const repos$ = this.http.get<Repo[]>('https://localhost:7204/api/Repos').subscribe(
      result => {
        this.subject.next(result);
      }
    );

  }

  bookmark(id: string, url: string) {
    localStorage.setItem(`ID${id}`, url);
    console.log('id = ' + id + ' url = ' + url);
    const urlToPutTo = `https://localhost:7204/api/Repos/${id}`;
    const httpOptions = {
      headers: new HttpHeaders({ 'Content-Type': 'application/json' })
    }
    const urlTopass = JSON.stringify(url);
    this.http.put<string>(urlToPutTo, urlTopass, httpOptions).subscribe();

  }
}
interface Repo {
  id: string;
  name: string;
  url: string;
  avatarUrl: number;
}
<form class="my-form data-form" [formGroup]="form">

  <mat-form-field appearance="outline">
    <input matInput type="text" name="searchTerm" placeholder="Search Term" formControlName="searchTerm">

  </mat-form-field>

  <button mat-raised-button color="primary" type="submit" (click)="onClick()">Search</button>

</form>


<p *ngIf="!repos$"><em>Loading...</em></p>

<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="repos$">
  <thead>
    <tr>
      <th>Id</th>
      <th>Name</th>      
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let repo of repos$ | async">
      <td>{{ repo.id }}</td>
      <td>{{ repo.name }}</td>
      <td><img src="{{repo.avatarUrl}}" width=50 height=50/>"</td>
      <td><button (click)="bookmark(repo.id, repo.url)">Bookmark</button></td>
    </tr>
  </tbody>
</table>
